
public class Conditionals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a = 10;
		int b = 10;
		
		if(a > b)
		{
			System.out.println("A is greater");
		}
		else if (b > a)
		{
			System.out.println("B is greater");
		}
		else
		{
			System.out.println("They may be equal");
		}
		
		
		
		
	}

}
